import React, { useState, useEffect } from 'react';

// Replace emoji with icon as needed
const navItems = [
  { label: 'Overview', icon: 'ðŸ ' },
  { label: 'To-do', icon: 'ðŸ“' },
  { label: 'In Progress', icon: 'â³' },
  { label: 'In Review', icon: 'ðŸ”' },
  { label: 'Completed', icon: 'âœ…' },
];

// Inline Zuntra Logo SVG (replace with your actual SVG if needed)
const ZuntraLogo = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="220" height="61" fill="none" viewBox="0 0 220 61">
  <path fill="url(#a)" d="M.176.357h219.069v60.286H.176z"/>
  <path fill="url(#b)" d="M.176.357h219.069v60.286H.176z"/>
  <path fill="url(#c)" d="M.176.357h219.069v60.286H.176z"/>
  <defs>
    <pattern id="a" width="1" height="1" patternContentUnits="objectBoundingBox">
      <use href="#d" transform="scale(.00388 .01408)"/>
    </pattern>
    <pattern id="b" width="1" height="1" patternContentUnits="objectBoundingBox">
      <use href="#d" transform="scale(.00388 .01408)"/>
    </pattern>
    <pattern id="c" width="1" height="1" patternContentUnits="objectBoundingBox">
      <use href="#e" transform="scale(.00388 .01408)"/>
    </pattern>
    <image id="d" width="258" height="71" data-name="image.png" href="data:image/png;base64," preserveAspectRatio="none"/>
    <image id="e" width="258" height="71" data-name="image(edit).png" href="data:image/png;base64," preserveAspectRatio="none"/>
  </defs>
</svg>

);

export const TaskNavbar = () => {
  const [active, setActive] = useState(0);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
      if (window.innerWidth > 768) setIsMenuOpen(false);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleItemClick = (index) => {
    setActive(index);
    if (isMobile) setIsMenuOpen(false);
  };

  const toggleMenu = () => setIsMenuOpen(prev => !prev);

  return (
    <>
      {/* Mobile Toggle Button */}
      {isMobile && (
        <button className="hamburger-toggle" onClick={toggleMenu}>
          {isMenuOpen ? 'âœ–' : 'â˜°'}
        </button>
      )}

      {/* Mobile Fullscreen Menu */}
      {isMobile && isMenuOpen && (
        <div className="fullscreen-menu">
          <div className="logo-wrapper">
            <ZuntraLogo />
          </div>
          <ul className="menu-list">
            {navItems.map((item, index) => (
              <li
                key={index}
                className={active === index ? 'active' : ''}
                onClick={() => handleItemClick(index)}
              >
                <span className="icon">{item.icon}</span>
                {item.label}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Desktop Horizontal Navbar */}
      {!isMobile && (
        <div className="desktop-navbar">
          <ul className="desktop-menu">
            {navItems.map((item, index) => (
              <li
                key={index}
                className={active === index ? 'active' : ''}
                onClick={() => handleItemClick(index)}
              >
                <span className="icon">{item.icon}</span>
                {item.label}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Styles */}
      <style jsx>{`
        .hamburger-toggle {
          position: fixed;
          top: 1rem;
          right: 1rem;
          z-index: 1001;
          font-size: 1.8rem;
          background: white;
          border: none;
          border-radius: 0.5rem;
          padding: 0.5rem 0.8rem;
          box-shadow: 0 0 6px rgba(0, 0, 0, 0.2);
          cursor: pointer;
        }

        .fullscreen-menu {
          position: fixed;
          top: 0;
          left: 0;
          width: 100vw;
          height: 100vh;
          background-color: #bbd3cc;
          z-index: 1000;
          display: flex;
          flex-direction: column;
          align-items: center;
          padding-top: 5rem;
        }

        .logo-wrapper {
          display: flex;
          justify-content: center;
          align-items: center;
          margin-bottom: 1rem;
        }

        .menu-list {
          list-style: none;
          padding: 0;
          margin: 0;
          width: 100%;
        }

        .menu-list li {
          padding: 1rem 2rem;
          font-size: 1.2rem;
          font-weight: 500;
          display: flex;
          align-items: center;
          cursor: pointer;
          transition: background 0.2s ease-in-out;
        }

        .menu-list li:hover {
          background-color: #a8c4ba;
        }

        .menu-list li.active {
          background-color: #9bb8ae;
        }

        .icon {
          margin-right: 0.5rem;
        }

        .desktop-navbar {
          background-color: #bbd3cc;
          border-radius: 0.75rem;
          padding: 1rem;
          display: flex;
          justify-content: center;
        }

        .desktop-menu {
          list-style: none;
          display: flex;
          justify-content: center;
          gap: 2rem;
          margin: 0;
          padding: 0;
        }

        .desktop-menu li {
          cursor: pointer;
          font-weight: 500;
          padding: 0.5rem 1rem;
          border-radius: 0.5rem;
          display: flex;
          align-items: center;
          transition: background 0.3s ease;
        }

        .desktop-menu li:hover {
          background-color: #a8c4ba;
        }

        .desktop-menu li.active {
          background-color: #9bb8ae;
        }
      `}</style>
    </>
  );
};





